import { useState } from "react";
import { useWorkoutsContext } from '../hooks/useWorkoutsContext';


const WorkoutForm = () => {
    const { dispatch } = useWorkoutsContext();
    const [title, setTitle] = useState('');
    const [load, setLoad] = useState('');
    const [reps, setReps] = useState('');
    const [error, setError] = useState(null);
    const [emptyFields, setEmptyFields] = useState([]);


    const handleSubmit = async (e) => {
        // default action is to refresh the page to prevent that
        e.preventDefault();
        //dummy workout object that we will send as the body of the request
        const workout = {title, load, reps};

        const response = await fetch('/api/workouts', {
            method: 'POST',
            body: JSON.stringify(workout),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        const json = await response.json();

        if (!response.ok) {
            setError(json.error);
            setEmptyFields(json.emptyFields);
        }
        if (response.ok) {
            setTitle('');
            setLoad('');
            setReps('');
            setError(null);
            setEmptyFields([]);
            console.log("New workout added!", json);
            dispatch({type: 'CREATE_WORKOUT', payload: json});
        }

        if (workout._id) {
            const updatedWorkout = { title, load, reps };
            const updateResponse = await fetch(`/api/workouts/${workout._id}`, {
                method: 'PUT',
                body: JSON.stringify(updatedWorkout),
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            const updateJson = await updateResponse.json();

            if (!updateResponse.ok) {
                setError(updateJson.error);
                setEmptyFields(updateJson.emptyFields);
            }
            if (updateResponse.ok) {
                console.log("Workout updated!", updateJson);
                dispatch({ type: 'UPDATE_WORKOUT', payload: updateJson });
            }
        }

    }

    return (
        <form className="create" onSubmit={handleSubmit}>
            <h3>Add a New Workout</h3>

            <label>Excersize Title</label>
            <input 
                type="text"
                onChange={(e)=> {setTitle(e.target.value)}}
                value={title}
                className={emptyFields.includes('title') ? 'error' : ''}/>

            <label>Load (in kg): </label>
            <input 
                type="number"
                onChange={(e)=> {setLoad(e.target.value)}}
                value={load}
                className={emptyFields.includes('load') ? 'error' : ''}/>

            <label>Reps: </label>
            <input 
                type="number"
                onChange={(e)=> {setReps(e.target.value)}}
                value={reps}
                className={emptyFields.includes('reps') ? 'error' : ''}/>

            <button>Add Workout</button>
            {error && <div className="error">{error}</div>}
        </form>
    )
}

export default WorkoutForm;
